import React from 'react'
import { Row, Col, Form, Input, Select, Divider } from 'antd'
import ReactQuill from 'react-quill'; // ES6
import 'react-quill/dist/quill.snow.css'; // ES6
import ArticlesContext from '../../ArticlesContext'
import ImageUpload from '../../../components/ImageUpload'

const FormItem = Form.Item
const { TextArea } = Input


/*
 * Custom "star" icon for the toolbar using an Octicon
 * https://octicons.github.io
 */
const CustomButton = () => <span className="octicon octicon-star" />;

/*
 * Event handler to be attached using Quill toolbar module (see line 73)
 * https://quilljs.com/docs/modules/toolbar/
 */
function insertStar() {
  const cursorPosition = this.quill.getSelection().index;
  this.quill.insertText(cursorPosition, "★");
  this.quill.setSelection(cursorPosition + 1);
}

/*
 * Custom toolbar component including insertStar button and dropdowns
 */
const CustomToolbar = () => (
  <div id="toolbar">
    <select className="ql-header" defaultValue={""} onChange={e => e.persist()}>
      <option value="1" />
      <option value="2" />
      <option selected />
    </select>
    <button className="ql-bold" />
    <button className="ql-italic" />
    <select className="ql-color">
      <option value="red" />
      <option value="green" />
      <option value="blue" />
      <option value="orange" />
      <option value="violet" />
      <option value="#d0d1d2" />
      <option selected />
    </select>
    <button className="ql-insertStar">
      <CustomButton />
    </button>
  </div>
);





class ArticleCreateForm extends React.Component {

  constructor (props) {
    super(props)

    this.state = {
      article_img: null,
      body: ''
    }
  }

  handleSubmit = (e) => {
    e.preventDefault()
  }

  componentDidMount () {
    this.props.articles.fetchTopics()
    this.props.articles.fetchTags()
  }

  onFileUpload = (path) => {
    this.setState({article_img: path})
  }

  bodyChange = (value) => {
    this.setState({ body: value })
  }

  render () {

    const { getFieldDecorator } = this.props.form
    const { topics, tags } = this.props.articles

    if(!topics || !tags) {
      return <div>Loading</div>
    }

    return (
      <Form onSubmit={this.handleSubmit}>
        <Row gutter={24}>
          <Col span={15}>
            <FormItem label={"Title"}>
              {getFieldDecorator('title',{
                rules: [{required: true, message: "Article Title..."}]
              })(
                <Input name={"article_title"} placeholder={"Title..."}  />
              )}
            </FormItem>
            <FormItem label={"Tag Line"}>
              {getFieldDecorator('tag_line',{
                rules: [{required: true, message: "Tag Line..."}]
              })(
                <Input name={"tag_line"} placeholder={"Tag Line..."}  />
              )}
            </FormItem>
            <Divider />
            <ReactQuill
              className="quill-editor"
              defaultValue={this.state.text}
              onChange={this.bodyChange} />
          </Col>
          <Col span={9}>
            <FormItem label={"Topic"}>
              {getFieldDecorator('topic',{
                rules: [{required: true, message: "Select a topic..."}]
              })(
                <Select name={"attribute_type"} onChange={this.handleAttributeChange}>
                  {topics.map(item => (
                    <Select.Option key={item.id} value={item.id}>
                      {item.title}
                    </Select.Option>
                  ))}
                </Select>
              )}
            </FormItem>
            <Divider />
            <ImageUpload token={this.props.articles.token} onUpload={this.onFileUpload}/>
            <Divider />
            <FormItem label={"Tags"}>
              {getFieldDecorator('tags',{
                rules: [{required: true, message: "sample, tag, goes, here"}]
              })(
                <Select
                  name={"tags"}
                  mode="tags"
                  onChange={this.handleAttributeChange}
                  tokenSeparators={[',']}
                >
                  {tags.map(item => (
                    <Select.Option key={item.id} value={item.name}>
                      {item.name}
                    </Select.Option>
                  ))}
                </Select>
              )}
            </FormItem>
          </Col>
        </Row>
      </Form>
    )
  }
}

const WrappedForm = Form.create()(ArticleCreateForm);

export default React.forwardRef((props, ref) => (
  <ArticlesContext.Consumer>
    {articles => <WrappedForm {...props} articles={articles} ref={ref} />}
  </ArticlesContext.Consumer>
))
